  methods: {
    next() {
      const slides = document
        .querySelector('.slider')
        .querySelectorAll('.slide')
      let curr
      ;[...slides].forEach((slide, i) => {
        if (window.getComputedStyle(slide).display === 'block') {
          curr = i + 1
          slide.classList = 'slide'
        }
      })
      if (curr == slides.length) {
        curr = 0
      }
      slides[curr].classList.add('show')
    },
    prev() {
      const slides = document
        .querySelector('.slider')
        .querySelectorAll('.slide')
      let curr
      ;[...slides].forEach((slide, i) => {
        if (window.getComputedStyle(slide).display === 'block') {
          slide.classList = 'slide'
          curr = i - 1
          console.log(curr)
        }
      })
      if (curr < 0) {
        curr = slides.length - 1
        console.log(curr)
      }
      slides[curr].classList.add('show')
    },
  },
}
